import Layout from "../components/MainLayout";
import Link from "next/link";

function getPosts() {
  return [
    { id: "hello-nextjs", title: "Total Client Visits", body: "Hello Next.js" },
    {
      id: "learn-nextjs",
      title: "Learn Next.js is awesome",
      body: "Hello Next.js"
    },
    {
      id: "deploy-nextjs",
      title: "Deploy apps with ZEIT",
      body: "Hello Next.js"
    }
  ];
}

function getSummaries() {
  return [
    {
      id: "total-hts-visits",
      title: "Total Client Visits",
      body: "Hello Next.js"
    },
    {
      id: "total-clients",
      title: "Learn Next.js is awesome",
      description: "Hello Next.js"
    },
    {
      id: "positive",
      title: "Deploy apps with ZEIT",
      description: "Hello Next.js"
    },
    {
      id: "negative",
      title: "Deploy apps with ZEIT",
      description: "Hello Next.js"
    }
  ];
}

const PostLink = ({ post }) => (
  <Card>
    <Link as={`/p/${post.id}`} href={`/post?title=${post.title}`}>
      <a>{post.title}</a>
    </Link>
    <style jsx>{`
      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
        font-family: "Arial";
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </Card>
);

export default function Dashboard() {
  return (
    <Layout>
      <h1>Dashboard</h1>
      <div>
        {getSummaries().map(summary => (
          <Card>
            <Card.Body>
              <Card.Title>{summary.title}</Card.Title>
              <Card.Text>{summary.body}</Card.Text>
            </Card.Body>
          </Card>
        ))}
      </div>
    </Layout>
  );
}

