import Header from "./Header";

export default class Layout extends React.Component {
  componentDidMount() {}

  componentsWillMount() {}

  render() {
    const { children, title } = this.props;

    return (
      <div>
        <Header />
        <main>{children}</main>
        <footer>foot</footer>
        <style jsx>{`
          main {
            width: 85%;
            margin: 0 auto;
          }
        `}</style>
      </div>
    );
  }
}
