import Layout from "../components/MainLayout";
import Link from "next/link";
import Card from "react-bootstrap/Card";

function getPosts() {
  return [
    { id: "hello-nextjs", title: "Total Client Visits", body: "Hello Next.js" },
    {
      id: "learn-nextjs",
      title: "Learn Next.js is awesome",
      body: "Hello Next.js"
    },
    {
      id: "deploy-nextjs",
      title: "Deploy apps with ZEIT",
      body: "Hello Next.js"
    }
  ];
}

const PostLink = ({ post }) => (
  <Card>
    <Link as={`/p/${post.id}`} href={`/post?title=${post.title}`}>
      <a>{post.title}</a>
    </Link>
    <style jsx>{`
      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
        font-family: "Arial";
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </Card>
);

export default function Blog() {
  return (
    <Layout>
      <h1>My blog</h1>
      <ul>
        {getPosts().map(post => (
          <PostLink key={post.id} post={post} />
        ))}
      </ul>
      <style jsx>{`
        h1,
        a {
          font-family: "Arial";
        }

        ul {
          padding: 0;
        }
      `}</style>
    </Layout>
  );
}
